PRAGMA defer_foreign_keys=TRUE;
CREATE TABLE day_schedules (
            date TEXT PRIMARY KEY,
            schedule TEXT NOT NULL CHECK (schedule IN ('A', 'B')),
            created_at DATETIME DEFAULT CURRENT_TIMESTAMP,
            updated_at DATETIME DEFAULT CURRENT_TIMESTAMP
        );
CREATE TABLE day_types (
            date TEXT PRIMARY KEY,
            type TEXT NOT NULL,
            created_at DATETIME DEFAULT CURRENT_TIMESTAMP,
            updated_at DATETIME DEFAULT CURRENT_TIMESTAMP
        );
CREATE TABLE events (
            id INTEGER PRIMARY KEY AUTOINCREMENT,
            school TEXT NOT NULL CHECK (school IN ('wlhs', 'wvhs')),
            date TEXT NOT NULL,
            title TEXT NOT NULL,
            department TEXT,
            time TEXT,
            description TEXT,
            created_at DATETIME DEFAULT CURRENT_TIMESTAMP,
            updated_at DATETIME DEFAULT CURRENT_TIMESTAMP
        );
CREATE TABLE materials (
            id INTEGER PRIMARY KEY AUTOINCREMENT,
            school TEXT NOT NULL CHECK (school IN ('wlhs', 'wvhs')),
            date TEXT NOT NULL,
            grade_level INTEGER NOT NULL CHECK (grade_level BETWEEN 9 AND 12),
            title TEXT NOT NULL,
            link TEXT NOT NULL,
            description TEXT DEFAULT '',
            password TEXT DEFAULT '',
            created_at DATETIME DEFAULT CURRENT_TIMESTAMP,
            updated_at DATETIME DEFAULT CURRENT_TIMESTAMP
        );
DELETE FROM sqlite_sequence;
CREATE INDEX idx_events_school_date ON events(school, date);
CREATE INDEX idx_materials_school_date_grade ON materials(school, date, grade_level);
CREATE INDEX idx_events_date ON events(date);
CREATE INDEX idx_materials_date ON materials(date);
CREATE INDEX idx_day_schedules_date ON day_schedules(date);
CREATE INDEX idx_day_types_date ON day_types(date);
